package com.repairtracker.dto.request;

import javax.validation.constraints.Email;
import javax.validation.constraints.NotBlank;
import javax.validation.constraints.NotNull;
import javax.validation.constraints.Size;
import lombok.Data;

@Data
public class RegisterRequest {
    // Frontend now uses email as unique identifier; username optional (autogenerated if missing)
    private String username;
    
    @NotBlank(message = "Email is required")
    @Email(message = "Email should be valid")
    private String email;
    
    @NotBlank(message = "Password is required")
    @Size(min = 6, message = "Password must be at least 6 characters")
    private String password;

    // Role coming from FE: "client" or "store"; map to enums
    @NotNull(message = "Role is required")
    private String role; // client | store

    // Client fields
    private String fullName;
    private String phone;

    // Store fields
    private String shopName;
    private String city;
    private String address;
}
